PRICE = [0, 1, 2, 1, 3, 1, 2, 1, 3, 1, 2, 1, 3, 1, 2, 1]

# Создаем массив для хранения минимальных очков для достижения каждого числа
n = 15
INF = 10**9  # Очень большое число как "бесконечность"
min_score = [INF] * (n + 1)  # от 0 до 15

# Начальное состояние: в числе 0, очков набрано Price[0] = 0
min_score[0] = PRICE[0]

# Перебираем все числа от 0 до 15
for i in range(n + 1):
    # Если в это число нельзя попасть, пропускаем
    if min_score[i] == INF:
        continue
    
    # Пробуем сделать шаги +1, +2, +4
    for step in [1, 2, 4]:
        next_num = i + step
        
        # Проверяем, не вышли ли за пределы
        if next_num <= n:
            # Очки за следующий шаг = текущие очки + очки за посещение next_num
            new_score = min_score[i] + PRICE[next_num]
            
            # Если нашли лучший способ добраться до next_num, обновляем
            if new_score < min_score[next_num]:
                min_score[next_num] = new_score

# Выводим минимальные очки для достижения числа 15
print(min_score[15])
